import React, { createContext, useState, useContext } from 'react';

const isLoginCotext = createContext();

export const isLogin = () => useContext(isLoginCotext);

export const IsLoginProvider = ({ children }) => {
  const [isLogin, setLogin] = useState(null);

  return (
    <IsLoginProvider.Provider value={{ isLogin, setLogin }}>
      {children}
    </IsLoginProvider.Provider>
  );
};

export default isLoginCotext;
