import React, { useState, useContext } from 'react';
import axios from 'axios';
import { useNavigate, Link, useParams } from 'react-router-dom';
import './styles/userManageStyles.css';
import RoleContext from '../components/RoleContext';
import IsLoginCotext from '../components/IsLoginContext'; 

const LoginPage = () => {
  const [email, setEmail] = useState('');
  const { userid } = useParams();
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null);
  const { setRole } = useContext(RoleContext);
  const { setLogin } = useContext(IsLoginCotext);
  const navigate = useNavigate();

  const handleSubmit = async (event) => {
    event.preventDefault(); 

    try {
      const response = await axios.post('/api/user/password-reset', { password, userid });

      if (response.status !== 200) {
        throw new Error('Login failed ..'); 
      } else if (response.status == 500) {
        throw new Error('Internal server error ..');
      }

      const data = response.data;
      if (!data || typeof data.role === 'undefined') {
        throw new Error('Invalid response data ..');
      }
      
      setRole(data.role);
      setLogin(true);

      if (data.role === 'user') {
        navigate('/user/home');
      } 
    } catch (error) {
      setError(error.message);
    }
  };

  return (
    <div className="login-form">
      <p1>Login</p1>
      {error && <p style={{ color: 'red', fontWeight: 'bold', marginBottom: '10px', width: '465px' }}>{error}</p>}
      <form onSubmit={handleSubmit}>
        <input
          type="password"
          id="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
          className="login-input"
        />
        <p>*fill all the required fields.</p>
        <button type="submit" className="login-button">Submit</button>
      </form>
 
    </div>
  );
};

export default LoginPage;
