import React, { useState, useContext } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';
import './styles/userManageStyles.css';
import RoleContext from '../components/RoleContext';
import isLoginCotext from '../components/isLoginContext'; 

const LoginPage = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null);
  const { setRole } = useContext(RoleContext);
  const { setLogin } = useContext(isLoginCotext);
  const navigate = useNavigate();

  const handleSubmit = async (event) => {
    event.preventDefault(); 

    try {
      const response = await axios.post('/api/user/login', { email, password });

      if (response.status !== 200) {
        throw new Error('Login failed ..'); 
      } else if (response.status == 500) {
        throw new Error('Internal server error ..');
      }

      const data = response.data;
      if (!data || typeof data.role === 'undefined') {
        throw new Error('Invalid response data ..');
      }
      
      setRole(data.role);
      setLogin(true);

      if (data.role === 'user') {
        navigate('/user/home');
      } 
    } catch (error) {
      setError(error.message);
    }
  };

  return (
    <div className="login-form">
      <P1>Login</P1>
      {error && <p className="error-message">{error}</p>}
      <form onSubmit={handleSubmit}>
        
        <input
          type="email"
          id="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
          className="login-input"
        />
        
        <input
          type="password"
          id="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
          className="login-input"
        />
        <P>*fill all the required fields.</P>
        <button type="submit" className="login-button">Login</button>
        <div>
            <P2>Don't have an account?</P2>
            <Link to="/user/register"><P3>Register</P3></Link>
        </div>
      </form>
 
    </div>
  );
};

export default LoginPage;
