import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import BookCard from '../components/BookCardView';
import { useLogin } from './IsLoginContext';
import { useRole } from '../components/RoleContext';
import axios from 'axios';

const BookView = () => {
    const [books, setBooks] = useState([]);
    const [error, setError] = useState(null);
    const { isLogin } = useLogin();
    const { role } = useRole();
    const [loading, setLoading] = useState(true);


    const getData = async () => {
        try {
            const response = await axios.get('/api/books/data');
            if (response.status === 200) {
                setBooks(response.data);
                setError(null);
            } else {
                throw new Error('Book fetching error ..');
            }
        } catch (error) {
            setError('Failed to fetch book data.');
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        if (isLogin === true && role === "user") {
            getData();
        } else {

        }  
    }, []);

    return (
        <div className="container d-flex justify-content-center flex-wrap">
            {loading && <p style={{ marginTop: '50px' }}>Loading...</p>}
            {error && (
                <div className="alert alert-danger" role="alert" style={{ marginTop: '50px' }}>
                    {error}
                </div>
            )}
            {Array.isArray(books) && books.map((book, index) => (
                <BookCard
                    key={index}
                    title={book.title}
                    imageFileName={book.imageFileName}
                    author={book.author}
                    fileName={book.fileName}
                />
            ))}
        </div>
    );
};

export default BookView;
